import "core-js/modules/es.array.push.js";
import router from "../router";
import { APIService } from "../http/APIService";
const apiService = new APIService();
export default {
  name: "SkillCreate",
  data() {
    return {
      showError: false,
      skill: {},
      pageTitle: "Add New Skill",
      isUpdate: false,
      showMsg: ""
    };
  },
  methods: {
    createSkill() {
      apiService.addNewSkill(this.skill).then(response => {
        if (response.status === 201) {
          this.skill = response.data;
          this.showMsg = "";
          router.push("/skill-list/new");
        } else {
          this.showMsg = "error";
        }
      }).catch(error => {
        this.showMsg = "error";
      });
    },
    cancelOperation() {
      router.push("/skill-list");
    },
    updateSkill() {
      apiService.updateSkill(this.skill).then(response => {
        if (response.status === 200) {
          this.skill = response.data;
          router.push("/skill-list/update");
        } else {
          this.showMsg = "error";
        }
      }).catch(error => {
        this.showMsg = "error";
      });
    }
  },
  mounted() {
    if (this.$route.params.pk) {
      this.pageTitle = "Edit Skill";
      this.isUpdate = true;
      apiService.getSkill(this.$route.params.pk).then(response => {
        this.skill = response.data;
      }).catch(error => {
        this.showMsg = "error";
      });
    }
  }
};
